channelEmbed.description:
$eval
// 1x1 image (used in questions with no image)
var noimage = "https://i.imgur.com/Qkum1e9.png"

// Questions (you CAN edit this!)
// Question syntax: ["question", "answer 1", "answer 2", "answer 3", "answer 4", correct answer, "image"] - see GitHub

var questions = [
["What's the capital city of Slovakia?", "Bratislava", "New York", "Prague", "Berlin", 1, "https://i.imgur.com/2UVQDfY.png"],

["What is the name of the character in the picture?", "Patrick", "Spongebob", "Squidward", "Mr. Krabs", 2, "https://upload.wikimedia.org/wikipedia/en/thumb/3/3b/SpongeBob_SquarePants_character.svg/640px-SpongeBob_SquarePants_character.svg.png"],

["What is the capital city of Australia?", "Sydney", "Melbourne", "Canberra", "Brisbane", 3, "https://i.imgur.com/oRGGv0h.png"],

["Who came up with the theory of relativity?", "Edgar Allan Poe", "Albert Einstein", "Galileo Galilei", "Louis Pasteur", 2, "https://cdn.mos.cms.futurecdn.net/rYUN9j8ZFyHbFeqDVCPkH5-1200-80.jpg"],

["What language is spoken in Brazil?", "Portuguese", "Spanish", "English", "French", 1, noimage],

["In what decade was the internet created?", "1960s", "1970s", "1980s", "1990s", 1, noimage],

["Which operating system uses this logo?", "Windows", "MacOS", "MS DOS", "Linux", 4, "https://i.imgur.com/s0W5Etb.jpeg"],

["Which is the correct way out of the maze in the picture?", "A", "B", "C", 3, "https://i.imgur.com/WeD7q1x.png"],

["A cloud weights around a million tonnes. True or false?", "True", "False", 1, noimage],

["Earth's rotation is always the same. True or false?", "True", "False", 2, "https://i.imgur.com/amY4BC4.jpeg"],

["Australia is wider than the moon. True or false?", "True", "False", 1, "https://i.imgur.com/sE5SDuF.jpeg"],

["What does a barometer measure?", "Sound", "Light", "Atmospheric pressure", "Humidity", 3, noimage],

["What is the capital city of Canada?", "Ottawa", "Vancouver", "Toronto", "Quebec City", 1, "https://i.imgur.com/Mj32x6J.png"],

["Competitive art was once an Olympic sport. True or false?", "True", "False", 1, noimage]
]

// Question generator
var gennum = Math.floor(Math.random() * parseInt(questions.length))

// Assign to a user [question, correct answer]
var game$ID = [gennum, questions[gennum][questions[gennum].length-2]]

// Reply message
var reply = questions[game$ID[0]][0].toString()

// Answer letters - if you want to add more answer options, just type "E: ", "F: " and so on
var letters = ["A: ", "B: ", "C: ", "D: "]

// Show a question and answers
for (let i = 1; i < questions[game$ID[0]].length-2; i++) reply += "\n- " + letters[i-1] + questions[game$ID[0]][i]

$halt $get(reply)
